import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import InputForms from './Components/InputForms';
import SignupForm from './Components/SignupForm';

// import { registerUser, verifyUser, loginUser } from './services/api_helper';


class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentUser: null

    }

  }

  handleRegister = async (event, user) => {
    event.preventDefault();
    const loadedUser = await registerUser(user);
    this.setState({
      currentUser: loadedUser
    })
  }

  async componentDidMount() {
    const currentUser = await verifyUser();
    if(currentUser) {
      this.setState({
        currentUser: currentUser
      })
    }
  }

  handleLogin = async (event, user) => {
    event.preventDefault();
   const loadedUser = await loginUser(user);

   this.setState({
     currentUser:loadedUser
   })
  }

  handleLogout = () => {
    this.setState({
      currentUser:null
    })
    localStorage.removeItem('authToken');
  }

  //Second thing that happens is the render() method
  render() {
  return (
    <div className="App">
      {this.state.currentUser && <h3>Welcome: {this.state.currentUser.username}</h3>}
      {this.state.currentUser ? <button onClick={this.handleLogout}>Logout</button> : (
        <div>
          <InputForms handleSubmit={this.handleRegister}/>
          <SignupForm handleSubmit={this.handleLogin}/>
        </div>
      )}
      <PostContainer />      
    </div>
  );
}
}
 
export default App;
