        return<UpdatePostForm
        posts={this.state.posts}
        updatePost={this.updatePost}
        postId={props.match.params.id}
        />
    }} /> */}
    <Cities />
    <PostList />
    <SinglePost />
    </div>

    ) 
}
}

export default withRouter (PostContainer);
